import { useState } from "react";

const InvestInput = (props) => {
    const [userInput, setUserInput] = useState(
        {
            "current-savings": "",
            "yearly-contribution": "",
            "expected-return": "",
            "duration": "",
        }
    );

    const inputChangeHandler = (event) => {
        const { name, value } = event.target;
        console.log(name, value);
        setUserInput({...userInput, [name]: value });
    };

    return (
        <div>
            <form className="form">
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">
                            Current Savings ($)
                        </label>
                        <input type="number" id="current-savings" onChange={inputChangeHandler}/>
                    </p>
                    <p>
                        <label htmlFor="yearly-contribution">
                            Yearly Savings ($)
                        </label>
                        <input type="number" id="yearly-contribution" />
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input type="number" id="expected-return" />
                    </p>
                    <p>
                        <label htmlFor="duration">
                            Investment Duration (years)
                        </label>
                        <input type="number" id="duration" />
                    </p>
                </div>
                <p className="actions">
                    <button type="reset" className="buttonAlt">
                        Reset
                    </button>
                    <button type="submit" className="button" onClick={props.onSubmit}>
                        Calculate
                    </button>
                </p>
            </form>
        </div>
    );
};

export default InvestInput;
