import styles from './ErrorModal.module.css'

const ErrorModal = (props) => {
    console.log('Error');

    let errorMessage;
    if(props.blank) {
        errorMessage = <p>blank error!</p>;
    } else if(props.lowAge) {
        errorMessage = <p>age error!</p>;
    }

    return (
        <div className={styles['backdrop']} onClick={props.onClick}>
            <div className={styles['modal']}>
                <div className={styles['header']}>
                    <h2>Invalid Input</h2>
                </div>
                <div className={styles['content']}>
                    {errorMessage}
                    <button styles={['action']} onClick={props.onClose}>Okay</button>
                </div>
            </div>
        </div>
    );
};

export default ErrorModal;
