import React, { useState } from "react";
import AddUser from "./components/AddUser/AddUser";
import UserList from "./components/UserList/UserList";
import ErrorModal from "./components/Result/ErrorModal";

function App() {
    const [userList, setUserList] = useState([]);
    const [blankError, setBlankError] = useState(false);
    const [ageError, setAgeError] = useState(false);

    const addUserHandler = (input) => {
        if(input.name === '' || input.age === '') {
            setBlankError(true);
        } else if(+input.age <= 0) {
            setAgeError(true);
        } else {
            setUserList((prev) => {
                const updatedUserList = [...prev];
                updatedUserList.unshift(input);
                console.log(updatedUserList);
                return updatedUserList;
            });
        }
    };

    return (
        <div>
            <AddUser onAdd={addUserHandler}/> 
            <UserList data={userList}/>
            {(blankError || ageError) && <ErrorModal/>}
        </div>
    );
}

export default App;
