import { useState, useRef } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const nameInputRef = useRef(""); // 값 변화할 때마다 state 변경 필요 없을 때
  const [enteredValueIsValid, setEnteredValueIsValid] = useState(false);

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    if(enteredName.trim().length === 0) {
      setEnteredValueIsValid(false);
      return;
    }
    setEnteredValueIsValid(true);


    const enteredValue = nameInputRef.current.value;

    // nameInputRef.current.value = ''; //js 코드로 dom 조작하는 것은 지양
    setEnteredName(""); // 입력된 값 초기화 하고 싶을 때는 state
  };

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="form-control">
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          ref={nameInputRef}
          value={enteredName}
        />
        {!enteredValueIsValid && <p>Name must be </p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
