// import { createStore } from "redux"; 
import { configureStore } from "@reduxjs/toolkit"; // 여러개의 reducer를 하나의 reducer로
import { createSlice } from "@reduxjs/toolkit";

const initialCounterState = {
  value: 0,
  showCounter: true,
};

const counterSlice = createSlice({
  name: 'counter',
  initialState: initialCounterState,
  reducers: { //reducer에서 조건문 쓸 필요 없음
    increment(state) {
      state.counter++; // 실제 state 조작하는 것처럼 보이지만, 내부에서 새로운 state 리턴해줌
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload
    },
    toggle(state) {
      state.counter = !state.counter;
    }
  },
});

const initialAuthState = {
  isAuthenticated: false,
};

const authSlice = createSlice({
  name:'authentication',
  initialState: initialAuthState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    },
  }
})


const store = configureStore({
  // reducer: counterSlice.reducer
  reducer: { // slice 여러개일 때 mapping하는 방식
    counter: counterSlice.reducer,
    auth: authSlice.reducer,
  }
});

export default store;

export const counterActions = counterSlice.actions;
// {type: 액션마다 다른 고유 식별자} 형식의 action 객체 생성
// 고유 식별자 생성, 오타 고민할 필요 없음!!
export const authActions = authSlice.actions;
