import {
  useNavigate,
  Form,
  useNavigation,
  useActionData,
  json,
  redirect
} from "react-router-dom";

import classes from "./EventForm.module.css";

function EventForm({ method, event }) {
  const navigate = useNavigate();
  const navigation = useNavigation();
  const isSubmitting = navigation.state === "submitting";
  const data = useActionData();

  function cancelHandler() {
    navigate("..");
  }

  return (
    <Form method={method} className={classes.form}>
      {/* Form 컴포넌트를 이용하여 요청을 action으로 제출 */}
      {/* 다른 라우트의 action으로 제출하기 위해 'action' property 이용 */}
      {/* method 소문자로 입력해도 action으로 제출하면서 대문자로 치환 */}
      {data && data.errors && (
        <ul>
          {Object.values(data.errors).map((err) => ( 
            <li key={err}>{err}</li>
          ))}
        </ul>
      )}
      {/*Object.values() => 객체를 배열으로*/}
      <p>
        <label htmlFor="title">Title</label>
        <input
          id="title"
          type="text"
          name="title"
          required
          defaultValue={event ? event.title : ""}
        />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input
          id="image"
          type="url"
          name="image"
          required
          defaultValue={event ? event.image : ""}
        />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input
          id="date"
          type="date"
          name="date"
          required
          defaultValue={event ? event.date : ""}
        />
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea
          id="description"
          name="description"
          rows="5"
          required
          defaultValue={event ? event.description : ""}
        />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler} disabled={isSubmitting}>
          Cancel
        </button>
        <button disabled={isSubmitting}>
          {isSubmitting ? "Submitting..." : "Save"}
        </button>
      </div>
    </Form>
  );
}

export default EventForm;

export async function action({request, params}) { // EventForm의 Form에서 제출된 data가 request로 옴
  const data = await request.formData();
  const eventData = { // name property로 입력된 값으로 탐색
    title: data.get('title'),
    image: data.get('image'),
    date: data.get('date'),
    description: data.get('description'),
  }
  const method = request.method

  let url = "http://localhost:8080/events";
  if (method === 'PATCH') {
    url = "http://localhost:8080/events/" + params.eventId;
  }

  const response = await fetch(url, {
    method: method,
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(eventData),
  });

  if(response.status === 422) {
    return response;
  }

  if (!response.ok) {
    console.log("!");
    throw json({message: 'could not save event', status: 500});
  }
  else {
    console.log("?");
    return redirect('/events'); // response 객체 생성해서 리턴   
  }
}
