import { useLoaderData, json, useRouteLoaderData, redirect, defer } from "react-router-dom";
import EventItem from "../components/EventItem";
import EventsList from "../components/EventsList";

function EventDetailPage() {
  // const params = useParams();
  const { event, events } = useRouteLoaderData('event-detail'); // 상위 route의 loader data 이용하기 위해

  return( 
    <>
      <Await resolve={event}>
        <EventItem event={event}/>
      </Await>
      <Await resolve={events}>
        <EventsList events={events} />  
      </Await>


    </>
  );
}

export default EventDetailPage;

async function loadEvent(id) {
  const response = await fetch("http://localhost:8080/events/" + id);
  if(!response.ok) {
    return json({message: 'could not fetch detail'}, {status: 500});
  } else {
    const resData = await response.json();
    return resData.events;
  }
};

async function loadEvents() {
  const response = await fetch("http://localhost:8080/events");
  if (!response.ok) {
    return json({ message: "could not fetch events" }, { status: 500 });
  } else {
    const resData = await response.json();
    return resData.events;
  }
};

export async function loader({request, params}) {
  const id = params.eventId;
  return defer({
    event: loadEvent(id),
    events: loadEvents(),
  });
};

export async function action({request, params}) {
  const eventId = params.eventId;
  const response = await fetch('http://localhost:8080/events/' + eventId, {
    // method: 'DELETE'
    method: request.method
  })
  if(!response.ok) {
    throw json({
      message: 'could not delete event'
    }, {status: 500});
  } else {
    return redirect('/events')
  }
};
